import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class SalaryService {

  constructor(private http: HttpClient,private router:) { }

  response: any;
  currentComponent: string = "form";

  PostData(UserInputData: any) {
    this.http.post('http://localhost:5204/Salary/IncreaseSalaryCalc', UserInputData, { observe: "response" }).
      subscribe({

        next: (res) => {
          console.log(res.body as any)
          this.response = res.body as any

        },
        error: (err) => {
          console.error(err)
        },
        complete: () => {
          console.log("data retrived successfully")
        }

      })


  }


}
