import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { IFormData } from 'src/app/Files/IFormData';
import { SalaryService } from 'src/app/services/salary.service';

@Component({
  selector: 'app-input-form',
  templateUrl: './input-form.component.html',
  styleUrls: ['./input-form.component.css']
})
export class InputFormComponent {

  constructor(private salary: SalaryService, private router: Router) { }

  //variables
  isExtraWork = false;
  submittedData: any;
  isSwitchEnabled = false; // Controls the switch
  selectedOption: string = ''; // Tracks the selected radio button value

  // form default template
  formData: IFormData = {
    jobType: 100,
    experience: 'junior',
    administrationLevel: 0,
    yearsOfExperience: 0,
    ExtraWorkTeam: 'C',
  }

  onExtraWorkToggle(): void {
    console.log('IsExtraWork:', this.isExtraWork);
  }


  onSubmit(form: any): void {
    console.log(this.formData)
    this.salary.PostData(this.formData);




    console.log('Form Data:', form.value);
  }




}
